#!/bin/bash -ue
bowtie2 -L 15 --norc --no-unal             -x appris_celegans_v1_new -q GSM1169543.1.unaligned.filter.fastq.gz             --threads 1             --al-gz GSM1169543.1.aligned.transcriptome_alignment.fastq.gz             --un-gz GSM1169543.1.unaligned.transcriptome_alignment.fastq.gz 						           2> GSM1169543.1.transcriptome_alignment.log             | samtools view -bS -             | samtools sort -@ 1 -o GSM1169543.1.transcriptome_alignment.bam             && samtools index -@ {task.cpus} GSM1169543.1.transcriptome_alignment.bam             && samtools idxstats -@ {task.cpus} GSM1169543.1.transcriptome_alignment.bam  >                GSM1169543.1.transcriptome_alignment.stats
